data GeneralCategory
  = UppercaseLetter       -- Lu: Letter, Uppercase
  | LowercaseLetter       -- Ll: Letter, Lowercase
  | TitlecaseLetter       -- Lt: Letter, Titlecase
  | ModifierLetter        -- Lm: Letter, Modifier
  | OtherLetter           -- Lo: Letter, Other
  | NonSpacingMark        -- Mn: Mark, Non-Spacing
  | SpacingCombiningMark  -- Mc: Mark, Spacing Combining
  | EnclosingMark         -- Me: Mark, Enclosing
  | DecimalNumber         -- Nd: Number, Decimal
  | LetterNumber          -- Nl: Number, Letter
  | OtherNumber           -- No: Number, Other
  | ConnectorPunctuation  -- Pc: Punctuation, Connector
  | DashPunctuation       -- Pd: Punctuation, Dash
  | OpenPunctuation       -- Ps: Punctuation, Open
  | ClosePunctuation      -- Pe: Punctuation, Close
  | InitialQuote          -- Pi: Punctuation, Initial quote
  | FinalQuote            -- Pf: Punctuation, Final quote
  | OtherPunctuation      -- Po: Punctuation, Other
  | MathSymbol            -- Sm: Symbol, Math
  | CurrencySymbol        -- Sc: Symbol, Currency
  | ModifierSymbol        -- Sk: Symbol, Modifier
  | OtherSymbol           -- So: Symbol, Other
  | Space                 -- Zs: Separator, Space
  | LineSeparator         -- Zl: Separator, Line
  | ParagraphSeparator    -- Zp: Separator, Paragraph
  | Control               -- Cc: Other, Control
  | Format                -- Cf: Other, Format
  | Surrogate             -- Cs: Other, Surrogate
  | PrivateUse            -- Co: Other, Private Use
  | NotAssigned           -- Cn: Other, Not Assigned
  deriving (Show, Eq, Ord, Enum, Bounded)

isControl :: Char -> Bool
isControl c =
  case generalCategory c of
    Control -> True
    _       -> False

isPrint :: Char -> Bool
isPrint c =
  case generalCategory c of
    LineSeparator      -> False
    ParagraphSeparator -> False
    Control            -> False
    Format             -> False
    Surrogate          -> False
    PrivateUse         -> False
    NotAssigned        -> False
    _                  -> True

isSpace :: Char -> Bool
isSpace c =
  generalCategory c == Space

isUpper :: Char -> Bool
isUpper c =
  case generalCategory c of
    UppercaseLetter -> True
    TitlecaseLetter -> True
    _               -> False

isLower :: Char -> Bool
isLower c =
  case generalCategory c of
    LowercaseLetter -> True
    _               -> False

isAlpha :: Char -> Bool
isAlpha c =
  case generalCategory c of
    UppercaseLetter -> True
    LowercaseLetter -> True
    TitlecaseLetter -> True
    ModifierLetter  -> True
    OtherLetter     -> True
    _               -> False

isAlphaNum :: Char -> Bool
isAlphaNum c =
  case generalCategory c of
    UppercaseLetter -> True
    LowercaseLetter -> True
    TitlecaseLetter -> True
    ModifierLetter  -> True
    OtherLetter     -> True
    DecimalNumber   -> True
    LetterNumber    -> True
    OtherNumber     -> True
    _               -> False

isNumber :: Char -> Bool
isNumber c =
  case generalCategory c of
    DecimalNumber -> True
    LetterNumber  -> True
    OtherNumber   -> True
    _             -> False

isMark :: Char -> Bool
isMark c =
  case generalCategory c of
    NonSpacingMark       -> True
    SpacingCombiningMark -> True
    EnclosingMark        -> True
    _                    -> False

isSeparator :: Char -> Bool
isSeparator c =
  case generalCategory c of
    Space              -> True
    LineSeparator      -> True
    ParagraphSeparator -> True
    _                  -> False

isPunctuation :: Char -> Bool
isPunctuation c =
  case generalCategory c of
    ConnectorPunctuation    -> True
    DashPunctuation         -> True
    OpenPunctuation         -> True
    ClosePunctuation        -> True
    InitialQuote            -> True
    FinalQuote              -> True
    OtherPunctuation        -> True
    _                       -> False

isSymbol :: Char -> Bool
isSymbol c =
  case generalCategory c of
    MathSymbol              -> True
    CurrencySymbol          -> True
    ModifierSymbol          -> True
    OtherSymbol             -> True
    _                       -> False

toTitle :: Char -> Char
toTitle c = convLU tcTable c

toUpper :: Char -> Char
toUpper c = convLU ucTable c

toLower :: Char -> Char
toLower c = convLU lcTable c

-- XXX We could build a search tree and use binary search.
-- The table is delta coded.  The first Int is the distance from
-- previous table entry.  The second is the length of the span-1.
-- The third is the offset to add doing the conversion.
convLU :: [(Int, Int, Int)] -> Char -> Char
convLU t c = conv 0 t
  where i = primOrd c
        conv _ [] = c
        conv o ((dl, dh, zd):lhds) | i < l = c
                                   | i <= h = chr (i + d)
                                   | otherwise = conv l lhds
                                   where l = o + dl
                                         h = l + dh
                                         d = zigZagDecode zd

-- Do
--   (x >> 1) ^ -(x & 1)
-- This puts the sign bit back.
-- Due to the range we know the incoming x is never negative.
zigZagDecode :: Int -> Int
zigZagDecode x = (x `unsafeShiftR` 1) `xor` (-(x .&. 1))

--
-- Same as in src/runtime/bfile.c.
-- Run Length Encoding for ASCII
-- Format
-- c                -  c         one ASCII character
-- 0x80+n c         -  n+1       repetitions of ASCII character c, n > 1
-- 0x80+n 0x80+m c  -  n*128+m+1 repetitions of ASCII character c
-- ... for longer run lengths
-- Non-ASCII (i.e., >= 128) has a very poor encoding:
-- 0x81 c-0x80
decompressRLE :: BS.ByteString -> BS.ByteString
decompressRLE = BS.pack . de 0 . BS.unpack
  where
    de :: Int -> [Word8] -> [Word8]
    de _ [] = []
    de 1 (x:xs) | x < 0x80  = x + 0x80 : de 0 xs
    de n (x:xs) | x < 0x80  = replicate (n + 1) x ++ de 0 xs
                | otherwise = de ((n * 128) + fromIntegral x - 0x80) xs

-- XXX Instead of having a totally decompressed (large!) bytestring,
-- we could build a search tree from the RLE encoding.
generalCategory :: Char -> GeneralCategory
generalCategory c =
  let i = primOrd c
  in  if i < 0 || i >= BS.length bytestringGCTable then
        NotAssigned
      else
        toEnum (fromEnum (BS.primBSindex bytestringGCTable i))

bytestringGCTable :: BS.ByteString
bytestringGCTable = decompressRLE compressedGCTable

